import React, { createContext, useContext, useState, useEffect } from 'react';
import { collection, onSnapshot, doc, setDoc, updateDoc } from 'firebase/firestore';
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, signOut } from 'firebase/auth';
import { db, auth } from '../utils/firebase';
import type { User, Post, Match } from '../types';

interface AppContextType {
    currentUser: User | null;
    users: User[];
    posts: Post[];
    matches: Match[];
    login: (email: string, password?: string) => Promise<boolean>;
    register: (user: Omit<User, 'id'>, password?: string) => Promise<void>;
    logout: () => Promise<void>;
    createPost: (post: Omit<Post, 'id' | 'createdAt' | 'status'>) => void;
    createMatch: (postId: string, mentorId: string) => void;
    acceptMatch: (matchId: string) => void;
    loginWithGoogle: (email: string, name: string, picture: string) => { isNewUser: boolean, user?: User };
}

const AppContext = createContext<AppContextType | undefined>(undefined);

export const AppProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [users, setUsers] = useState<User[]>([]);
    const [posts, setPosts] = useState<Post[]>([]);
    const [matches, setMatches] = useState<Match[]>([]);

    const [currentUser, setCurrentUser] = useState<User | null>(() => {
        const saved = localStorage.getItem('edu_currentUser');
        return saved ? JSON.parse(saved) : null;
    });

    useEffect(() => {
        const unsubscribeUsers = onSnapshot(collection(db, 'users'), (snapshot) => {
            setUsers(snapshot.docs.map(doc => doc.data() as User));
        });

        const unsubscribePosts = onSnapshot(collection(db, 'posts'), (snapshot) => {
            const fetchedPosts = snapshot.docs.map(doc => doc.data() as Post);
            // Sort posts by newest first
            setPosts(fetchedPosts.sort((a, b) => b.createdAt - a.createdAt));
        });

        const unsubscribeMatches = onSnapshot(collection(db, 'matches'), (snapshot) => {
            setMatches(snapshot.docs.map(doc => doc.data() as Match));
        });

        return () => {
            unsubscribeUsers();
            unsubscribePosts();
            unsubscribeMatches();
        };
    }, []);

    useEffect(() => {
        if (currentUser) {
            localStorage.setItem('edu_currentUser', JSON.stringify(currentUser));

            // Re-sync current user from live db data if it updates
            const dbUser = users.find(u => u.id === currentUser.id);
            if (dbUser && JSON.stringify(dbUser) !== JSON.stringify(currentUser)) {
                setCurrentUser(dbUser);
            }
        } else {
            localStorage.removeItem('edu_currentUser');
        }
    }, [currentUser, users]);

    const login = async (email: string, password?: string) => {
        const user = users.find(u => u.email === email);
        if (user) {
            if (password) {
                try {
                    await signInWithEmailAndPassword(auth, email, password);
                    setCurrentUser(user);
                    return true;
                } catch (error) {
                    console.error("Login error", error);
                    return false; // Authentication failed
                }
            } else {
                // This branch allows for Google login or legacy passwordless logins to still work
                setCurrentUser(user);
                return true;
            }
        }
        return false;
    };

    const register = async (userData: Omit<User, 'id'>, password?: string) => {
        let userId = Date.now().toString();

        if (password) {
            // Let Firebase Auth handle the secure creation and assign a secure UID
            const userCredential = await createUserWithEmailAndPassword(auth, userData.email, password);
            userId = userCredential.user.uid;
        }

        const newUser: User = { ...userData, id: userId };
        try {
            await setDoc(doc(db, 'users', newUser.id), newUser);
            setCurrentUser(newUser); // Set after successful DB write
        } catch (error) {
            console.error("Error registering user: ", error);
            throw error;
        }
    };

    const logout = async () => {
        setCurrentUser(null);
        try {
            await signOut(auth);
        } catch (error) {
            console.error("Error signing out: ", error);
        }
    };

    const loginWithGoogle = (email: string, _name: string, picture: string) => {
        let user = users.find(u => u.email === email);
        if (!user) {
            // User does not exist, return flag so Auth.tsx can prompt for College ID
            return { isNewUser: true };
        }

        if (picture && !user.profilePic) {
            // Update profile pic if it was missing
            const updatedUser = { ...user, profilePic: picture };
            setCurrentUser(updatedUser);
            updateDoc(doc(db, 'users', user.id), { profilePic: picture }).catch(console.error);
            return { isNewUser: false, user: updatedUser };
        }

        setCurrentUser(user);
        return { isNewUser: false, user };
    };

    const createPost = async (postData: Omit<Post, 'id' | 'createdAt' | 'status'>) => {
        const newPost: Post = {
            ...postData,
            id: `p${Date.now()}`,
            createdAt: Date.now(),
            status: 'open'
        };

        try {
            await setDoc(doc(db, 'posts', newPost.id), newPost);

            // Notify others if this is a doubt based on College Name
            if (postData.type === 'doubt' && currentUser && currentUser.collegeName) {
                const peerEmails = users
                    .filter(u => u.id !== currentUser.id && u.collegeName?.toLowerCase() === currentUser.collegeName?.toLowerCase())
                    .map(u => u.email);

                if (peerEmails.length > 0) {
                    fetch('/api/send-email', {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify({
                            to: peerEmails,
                            subject: `EduBridge: Someone at your college needs help with ${newPost.subject}!`,
                            html: `
                                <div style="font-family: sans-serif; padding: 20px; color: #333;">
                                    <h2 style="color: #6366f1;">New Doubt Posted!</h2>
                                    <p><strong>${currentUser.name}</strong> from your college (${currentUser.collegeName}) just posted a doubt and is looking for a mentor.</p>
                                    <div style="background: #f3f4f6; padding: 15px; border-radius: 8px; margin: 20px 0;">
                                        <h3 style="margin-top: 0; color: #111;">${newPost.title}</h3>
                                        <span style="display: inline-block; background: #e0e7ff; color: #4338ca; padding: 4px 8px; border-radius: 4px; font-size: 12px; font-weight: bold; margin-bottom: 10px;">SUBJECT: ${newPost.subject.toUpperCase()}</span>
                                        <p style="margin-bottom: 0;">${newPost.description}</p>
                                    </div>
                                    <p>If you know the answer, log into EduBridge and click "Offer Help" on their post.</p>
                                    <a href="https://edu-bridge-sage-three.vercel.app" style="display: inline-block; background: #6366f1; color: white; padding: 10px 20px; text-decoration: none; border-radius: 6px; font-weight: bold; margin-top: 10px;">Open EduBridge</a>
                                </div>
                            `
                        })
                    }).catch(err => console.error('Failed to trigger email API', err));
                }
            }
        } catch (error) {
            console.error("Error creating post:", error);
        }
    };

    const createMatch = async (postId: string, mentorId: string) => {
        const post = posts.find(p => p.id === postId);
        if (!post) return;

        const newMatch: Match = {
            id: `m${Date.now()}`,
            postId,
            learnerId: post.authorId,
            mentorId,
            status: 'pending'
        };

        try {
            await setDoc(doc(db, 'matches', newMatch.id), newMatch);
        } catch (error) {
            console.error("Error creating match: ", error);
        }
    };

    const acceptMatch = async (matchId: string) => {
        const match = matches.find(m => m.id === matchId);
        if (match) {
            try {
                // Perform concurrent updates
                await Promise.all([
                    updateDoc(doc(db, 'matches', matchId), { status: 'accepted' }),
                    updateDoc(doc(db, 'posts', match.postId), { status: 'matched' })
                ]);
            } catch (error) {
                console.error("Error accepting match: ", error);
            }
        }
    };

    return (
        <AppContext.Provider value={{ users, currentUser, posts, matches, login, register, logout, createPost, createMatch, acceptMatch, loginWithGoogle }}>
            {children}
        </AppContext.Provider>
    );
};

export const useAppContext = () => {
    const context = useContext(AppContext);
    if (context === undefined) {
        throw new Error('useAppContext must be used within an AppProvider');
    }
    return context;
};

import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import { useAppContext } from '../context/AppContext';

const Auth: React.FC = () => {
    const [params] = useSearchParams();
    const navigate = useNavigate();
    const { login, register, currentUser, loginWithGoogle } = useAppContext();

    const [isLogin, setIsLogin] = useState(params.get('tab') !== 'register');
    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [collegeId, setCollegeId] = useState('');
    const [collegeName, setCollegeName] = useState('');
    const [userType, setUserType] = useState<'teacher' | 'student'>('student');
    const [subjectInput, setSubjectInput] = useState('');
    const [password, setPassword] = useState('');
    const [isGoogleSignIn, setIsGoogleSignIn] = useState(false);
    const [googleProfilePic, setGoogleProfilePic] = useState<string | undefined>(undefined);

    const [error, setError] = useState('');

    useEffect(() => {
        if (currentUser) {
            navigate('/dashboard');
        }
    }, [currentUser, navigate]);

    const handleGoogleSuccess = (credentialResponse: any) => {
        try {
            const decoded: any = jwtDecode(credentialResponse.credential);
            const result = loginWithGoogle(decoded.email, decoded.name, decoded.picture);

            if (result.isNewUser) {
                // Pre-fill the form and switch to registration
                setEmail(decoded.email);
                setName(decoded.name);
                setGoogleProfilePic(decoded.picture);
                setIsLogin(false);
                setIsGoogleSignIn(true);
                setError('Please complete your profile to finish signing up with Google.');
            } else {
                navigate('/dashboard');
            }
        } catch (err) {
            console.error('Error decoding Google JWT', err);
            setError('Google Login failed. Please try again.');
        }
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');

        if (isLogin) {
            try {
                const success = await login(email, password);
                if (!success) {
                    setError('Invalid email or password. Please try again or register.');
                } else {
                    navigate('/dashboard');
                }
            } catch (err: any) {
                setError(err.message || 'Login failed.');
            }
        } else {
            if (!name || !email || !collegeId || !collegeName || (!isLogin && !subjectInput) || (!isGoogleSignIn && !password)) {
                setError('Please fill in all required fields including your password.');
                return;
            }

            // Basic college email validation (e.g., must end in .edu or .ac.in or similar)
            if (!email.includes('.edu') && !email.includes('.ac.')) {
                setError('Please use a valid college email address (e.g. ending in .edu or .ac.in)');
                return;
            }

            // Registration ID format validation removed as per request

            const subjects = subjectInput.split(',').map(s => s.trim()).filter(s => s);
            try {
                await register({
                    name,
                    email,
                    role: 'user', // Default role since everyone is a mentor/learner now
                    userType,
                    subjects,
                    collegeId,
                    collegeName,
                    profilePic: googleProfilePic,
                    rating: 5.0 // Default rating for everyone since they can all mentor
                }, isGoogleSignIn ? undefined : password);
                navigate('/dashboard');
            } catch (err: any) {
                if (err.code === 'auth/email-already-in-use') {
                    setError('Email is already in use. Please sign in.');
                } else if (err.code === 'auth/weak-password') {
                    setError('Password is too weak. Please use at least 6 characters.');
                } else {
                    setError(err.message || 'Registration failed.');
                }
            }
        }
    };

    return (
        <div className="flex justify-center items-center" style={{ minHeight: '70vh' }}>
            <div className="glass-panel animate-fade-in" style={{ width: '100%', maxWidth: '450px', padding: '2.5rem' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '2rem', fontSize: '2rem' }}>
                    {isLogin ? 'Welcome Back' : 'Join EduBridge'}
                </h2>

                {error && (
                    <div className="badge badge-warning" style={{ display: 'block', textAlign: 'center', marginBottom: '1.5rem', padding: '0.75rem' }}>
                        {error}
                    </div>
                )}

                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '1.5rem' }}>
                    <GoogleLogin
                        onSuccess={handleGoogleSuccess}
                        onError={() => setError('Google Login Failed')}
                        useOneTap
                        theme="filled_black"
                        shape="pill"
                    />
                </div>

                <div style={{ textAlign: 'center', margin: '1.5rem 0', color: 'var(--text-muted)', position: 'relative' }}>
                    <div style={{ position: 'absolute', top: '50%', left: 0, right: 0, borderTop: '1px solid var(--border-color)', zIndex: 1 }}></div>
                    <span style={{ background: 'var(--color-surface)', padding: '0 10px', position: 'relative', zIndex: 2, fontSize: '0.875rem' }}>OR CONTINUE WITH EMAIL</span>
                </div>

                <form onSubmit={handleSubmit}>
                    {!isLogin && (
                        <div className="input-group">
                            <label>Full Name</label>
                            <input
                                type="text"
                                className="input-field"
                                placeholder="John Doe"
                                value={name}
                                onChange={e => setName(e.target.value)}
                            />
                        </div>
                    )}

                    <div className="input-group">
                        <label>College Email Address</label>
                        <input
                            type="email"
                            className="input-field"
                            placeholder="you@university.edu"
                            value={email}
                            onChange={e => setEmail(e.target.value)}
                            required
                        />
                    </div>

                    {!isGoogleSignIn && (
                        <div className="input-group">
                            <label>Password</label>
                            <input
                                type="password"
                                className="input-field"
                                placeholder="••••••••"
                                value={password}
                                onChange={e => setPassword(e.target.value)}
                                required
                            />
                        </div>
                    )}

                    {!isLogin && (
                        <>
                            <div className="input-group">
                                <label>College / University Name</label>
                                <input
                                    type="text"
                                    className="input-field"
                                    placeholder="e.g. Stanford University"
                                    value={collegeName}
                                    onChange={e => setCollegeName(e.target.value)}
                                    required
                                />
                            </div>

                            <div className="input-group">
                                <label>College ID Number {userType === 'teacher' && '(or Employee ID)'}</label>
                                <input
                                    type="text"
                                    className="input-field"
                                    placeholder={userType === 'teacher' ? "e.g. EMP1234" : "e.g. 21BCE1234"}
                                    value={collegeId}
                                    onChange={e => setCollegeId(e.target.value)}
                                    required
                                />
                            </div>

                            <div className="input-group">
                                <label>I am a...</label>
                                <div className="flex gap-4" style={{ marginTop: '0.5rem' }}>
                                    <label className="flex items-center gap-2" style={{ cursor: 'pointer', flex: 1, padding: '1rem', border: '1px solid var(--border-color)', borderRadius: 'var(--radius-md)', background: userType === 'student' ? 'rgba(99, 102, 241, 0.2)' : 'transparent', transition: 'var(--transition-fast)' }}>
                                        <input
                                            type="radio"
                                            checked={userType === 'student'}
                                            onChange={() => setUserType('student')}
                                            style={{ accentColor: 'var(--color-primary)' }}
                                        />
                                        Student
                                    </label>
                                    <label className="flex items-center gap-2" style={{ cursor: 'pointer', flex: 1, padding: '1rem', border: '1px solid var(--border-color)', borderRadius: 'var(--radius-md)', background: userType === 'teacher' ? 'rgba(99, 102, 241, 0.2)' : 'transparent', transition: 'var(--transition-fast)' }}>
                                        <input
                                            type="radio"
                                            checked={userType === 'teacher'}
                                            onChange={() => setUserType('teacher')}
                                            style={{ accentColor: 'var(--color-primary)' }}
                                        />
                                        Teacher
                                    </label>
                                </div>
                            </div>
                        </>
                    )}

                    {!isLogin && (
                        <div className="input-group">
                            <label>Subjects you can help with or want to learn (Comma separated)</label>
                            <input
                                type="text"
                                className="input-field"
                                placeholder="e.g. Calculus, Physics, Python"
                                value={subjectInput}
                                onChange={e => setSubjectInput(e.target.value)}
                            />
                        </div>
                    )}

                    <button type="submit" className="btn btn-primary" style={{ width: '100%', marginTop: '1rem', padding: '1rem' }}>
                        {isLogin ? 'Sign In' : 'Create Account'}
                    </button>
                </form>

                <p style={{ textAlign: 'center', marginTop: '2rem', color: 'var(--text-muted)' }}>
                    {isLogin ? "Don't have an account? " : "Already have an account? "}
                    <button
                        type="button"
                        onClick={() => setIsLogin(!isLogin)}
                        style={{ background: 'none', border: 'none', color: 'var(--color-primary)', fontWeight: 600, cursor: 'pointer', fontSize: '1rem' }}
                    >
                        {isLogin ? 'Register now.' : 'Sign in.'}
                    </button>
                </p>
            </div>
        </div>
    );
};

export default Auth;
